DROP TABLE IF EXISTS `situaciones_revistas`;
CREATE TABLE `situaciones_revistas` (
    `id` INT(11) NOT NULL COMMENT 'Identico a `id_situacion_revista` funciona como alias.',
    `id_situacion_revista` INT(11) NOT NULL COMMENT 'Esta tabla cumple la funcion de replicar los modelos de contrato dentro del sistema SIGARHU. Por ese motivo no tiene AUTO_INCREMENT ni ID propio.',
    `id_modalidad_vinculacion` INT(11) NOT NULL,
    `nombre` VARCHAR(255),
    `borrado` TINYINT(1) DEFAULT '0',
    PRIMARY KEY (`id_situacion_revista`),
    KEY `IDX_situaciones_revistas` (`id_modalidad_vinculacion` , `id_situacion_revista`)
)  ENGINE=INNODB AUTO_INCREMENT=1 DEFAULT CHARSET=UTF8;

INSERT INTO `situaciones_revistas` (id, id_modalidad_vinculacion, id_situacion_revista, nombre) 
	SELECT 
		`sr`.`id` AS id_situacion_revista,
		`mv`.`id` AS id_modalidad_vinculacion, 
        `sr`.`id` AS id_situacion_revista, 
        CONCAT(`mv`.`nombre`,' - ', `sr`.`nombre`) AS nombre
	FROM `sigarhu`.`convenio_situacion_revista` AS sr 
    INNER JOIN `sigarhu`.`convenio_modalidad_vinculacion` AS mv ON (`sr`.`id_modalidad_vinculacion` = `mv`.`id`)
    ORDER BY `mv`.`id` ASC;

DROP TABLE IF EXISTS `log_actualizaciones_sigarhu`;

CREATE TABLE `log_actualizaciones_sigarhu` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cuit`  bigint unsigned,
  `id_empleado_sigarhu` int(11),
  `id_empleado_control_accesos` int(11),
  `fecha`	TIMESTAMP,
  `descripcion`	LONGTEXT,
  PRIMARY KEY (`id`),
  KEY `IDX_log_actualizaciones_sigarhu` (`id_empleado_sigarhu`, `id_empleado_control_accesos`, `cuit`, `fecha`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;


